import React, {Component} from 'react';
import './app.css';
import  TodoList from  '../todo-list/todo-list';
import AppHeader from '../app-header/app-header';
import SearchPanel from  '../search-panel/search-panel';
import Filter from  '../filter/filter';
import NewItem from  '../new-item/new-item';

export default class App extends Component {
  constructor () {
    super();

    this.state = {
      todoData : [
        {
          label: 'Reply email',
          important: false,
          done: false,
          group: 'Todo',
        },
        {
          label: 'Meet Sarah',
          important: false,
          done: false,
          group: 'Todo',
        },
        {
          label: 'Make dinner',
          important: false,
          done: false,
          group: 'Todo',
        },
        {
          label: 'Milk',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          label: 'Meet',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          label: 'Book',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          label: '',
          important: false,
          done: false,
          group: 'Todo',
        }
      ],
    }
  }

  render () {
    return (
    <main className="app">
      <AppHeader />
      <div className='app__search-filter'>
        <SearchPanel />
        <Filter />
      </div>
      <TodoList />
      <NewItem />
    </main>);
  }
}
