import React, {Component} from 'react';
import './app.css';
import  TodoList from  '../todo-list/todo-list';
import AppHeader from '../app-header/app-header';
import SearchPanel from  '../search-panel/search-panel';
import Filter from  '../filter/filter';
import NewItem from  '../new-item/new-item';

export default class App extends Component {
  constructor () {
    super();

    this.state = {
      todoData : [
        {
          id:1,
          label: 'Reply email',
          important: false,
          done: true,
          group: 'Todo',
        },
        {
          id: 2,
          label: 'Meet Sarah',
          important: true,
          done: false,
          group: 'Todo',
        },
        {
          id: 3,
          label: 'Make dinner',
          important: false,
          done: false,
          group: 'Todo',
        },
        {
          id: 4,
          label: 'Milk',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          id: 5,
          label: 'Meet',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          id: 6,
          label: 'Book',
          important: false,
          done: false,
          group: 'To buy',
        },
        {
          id: 7,
          label: 'Walk with kids',
          important: false,
          done: false,
          group: 'Family',
        }
      ],
    }

    this.labelClickHandler = (id) => {
      this.setState(({todoData}) => {
        const indx = todoData.findIndex((item) => item.id === id);

        const newData = [
          ...todoData.slice(0, indx),
          {...todoData[indx], done: !todoData[indx].done},
          ...todoData.slice(indx + 1),
        ];

        return {
          todoData: newData,
        }
      })
    }

    this.importantClickHandler = (id) => {
      this.setState(({todoData}) => {
        const indx = todoData.findIndex((item) => item.id === id);

        const newData = [
          ...todoData.slice(0, indx),
          {...todoData[indx], important: !todoData[indx].important},
          ...todoData.slice(indx + 1),
        ];

        return {
          todoData: newData,
        }
      })
    }

    this.deleteClickHandler = (id) => {
      this.setState(({todoData}) => {
        const indx = todoData.findIndex((item) => item.id === id);

        const newData = [
          ...todoData.slice(0, indx),
          ...todoData.slice(indx + 1),
        ];

        return {
          todoData: newData,
        }
      })
    }
  }

  render () {

    const groups = Array.from(new Set(data.map((item)=> item.group)));
    return (
    <main className="app">
      <AppHeader />
      <div className='app__search-filter'>
        <SearchPanel />
        <Filter />
      </div>
      <TodoList
      data={this.state.todoData}
      onLabelClick={this.labelClickHandler}
      onImportantBtnClick={this.importantClickHandler}
      onDeleteButtonClick={this.deleteClickHandler}
      />
      <NewItem groups={groups}/>
    </main>);
  }
}
