import React, {Component} from 'react'
import './new-item.css';

export default class NewItem extends Component {
  constructor () {
    super();

    this.state = {
      label: '',
      groupValue: 'Todo',
    }

    this.onLabelChange = (evt) => {
      this.setState({
        label: evt.target.value,
      })
    } 

    this.onFormSubmit = (evt) => {
      evt.preventDefault();
      const {label, groupValue} = this.state;
      this.props.onNewItemSubmit(this.state.label, this.state.groupValue);
      this.setState({label: ''});
    }

    this.onGroupChange = (evt) => {
      this.setState({groupValue: evt.target.value});
    }
  }

  render() {
    const {groups} = this.props;

    const {groupValue} = this.state;

    const groupsMarkup = groups.map((group) => (
      <React.Fragment key={group}>
        <input type='radio' name='group' value={group}
          id={group}
          onChange={this.onGroupChange}
          checked = {groupValue === group}
        />
        <label htmlFor={group}>{group}</label>
      </React.Fragment>
    ));

    return (
    <form className='new-item'
      onSubmit={this.onFormSubmit}
    >
      <input
        type='text' placeholder='what need to be done'
        onChange={this.onLabelChange}
        value={this.state.label}
      />
      {groupsMarkup}
      <button type='submit'>Add task</button>
    </form>
    );
  }
}
