import React, {Component} from 'react'
import './new-item.css';

export default class NewItem extends Component {
  constructor () {
    super();

    this.state = {
      label: '',
      group: '',
    }

    this.onLabelChange = (evt) => {
      this.setState({
        label: evt.target.value,
      })
    } 

    this.onFormSubmit = (evt) => {
      evt.preventDefault();
      this.props.onNewItemSubmit(evt.target.value, this.state.group);
      this.setState({label: ''});
    }
  }

  render() {

    return (
    <form className='new-item'
    onSubmit={this.onFormSubmit}
    >
      <input
        type='text' placeholder='what need to be done'
        onChange={this.onLabelChange}
        value={this.state.label}
      />
      <input type='radio' name='group' value='todo' defaultChecked />
      <input type='radio' name='group' value='to buy' />
      <input type='radio' name='group' value='social' />
      <button type='submit'>Add task</button>
    </form>
    );
  }
}
