import React from 'react';
import './todo-group.css';
import TodoItem from '../todo-item/todo-item';

const TodoGroup = ({group, tasks, color, onLabelClick}) => {

  const style = {
    backgroundColor: color,
  };

  const todoGroupMarkup = tasks.map((task)=> (
    <li key={task.id}>
      <TodoItem
        label={task.label}
        onLabelClick={()=>onLabelClick(task.id)}
        done={task.done}
        important={task.important}
      />
      <div className='todo-group__buttons'>
        <button className='todo-group__button fa fa-exclamation'></button>
        <button className='todo-group__button fa fa-trash'></button>
      </div>
    </li>
  ));

  return (<div className='todo-group'>
    <h3 style={style}>{group}</h3>
    <ul className='todo-group__list'>
      {todoGroupMarkup}
    </ul>
  </div>);
};

export default TodoGroup;
